mapper.update:
update sys_role
set role_name = #{roleName,jdbcType=VARCHAR},
role_desc = #{roleDesc,jdbcType=VARCHAR},
role_valid = #{roleValid,jdbcType=BIT}
where role_id = #{roleId,jdbcType=BIGINT}

mapper.select:
select role_id, role_name, role_desc, role_valid
from sys_role
where role_id = #{roleId,jdbcType=BIGINT}
